import React from 'react';
import '../index.css';

function Folder({ isActive, setActive }) {
  const toggleClass = (id) => {
    setActive({ isActive, [id]: !isActive[id] });
  };

  const folders = [
    { id: 1, name: 'Compétences', img: './Icons/folder.png' },
    { id: 2, name: 'Formations', img: './Icons/folder.png' },
  ];
  return (
    <div className="folder">
      {folders.map((folder) => (
        <div
          className="folder-content"
          key={folder.id}
          onClick={toggleClass(folder.id)}
        >
          <div className="folder-icon">
            <img className="i-folder" src={folder.img} alt="folder" />
          </div>
          <div className="folder-name">{folder.name}</div>
        </div>
      ))}
    </div>
  );
}

export default Folder;
