import React, { useState } from 'react';
import '../index.css';
import Hammer from './Icon/hammer.png';

function CompetencesWindow() {
  const [categoryName, setCategoryName] = useState();

  const skillData = [
    {
      id: 1,
      name: 'Swift',
      img: '/img/crypto.png',
      category: 'Développement iOS',
    },
    {
      id: 2,
      name: 'Swift UI',
      img: '/img/crypto.png',
      category: 'Développement iOS',
    },
    {
      id: 3,
      name: 'React JS',
      img: '/img/crypto.png',
      category: 'Devéloppement Web',
    },
    {
      id: 4,
      name: 'JavaScript',
      img: '/img/crypto.png',
      category: 'Devéloppement Web',
    },
    {
      id: 5,
      name: 'CSS',
      img: '/img/crypto.png',
      category: 'Devéloppement Web',
    },
    {
      id: 6,
      name: 'SASS',
      img: '/img/crypto.png',
      category: 'Librairie / Frameworks',
    },
    {
      id: 7,
      name: 'Bootstrap',
      img: '/img/crypto.png',
      category: 'Librairie / Frameworks',
    },
    {
      id: 8,
      name: 'Git / Github',
      img: '/img/crypto.png',
      category: 'Versionning',
    },
    {
      id: 9,
      name: 'Figma',
      img: '/img/crypto.png',
      category: 'Software',
    },
  ];

  const categorizedData = skillData.reduce((acc, curr) => {
    const { id, Category, Name } = curr;

    if (!acc[Category]) {
      acc[Category] = {
        items: [],
      };
    }
    acc[Category].items.push(Name);

    return acc;
  }, {});

  console.log(categorizedData);


  return (
    <div className="apple-window-container">
      <div className="sidebar">
        <div className="traffic-lights">
          <div className="red"></div>
          <div className="orange"></div>
          <div className="green"></div>
        </div>
        <div className="sidebar-menu">
          <div className="sidebar-title">
            <div className="sidebar-project">
              <div className="project-name">
                <img className="menubar" src={Hammer} alt="menubar-icon" />
                <p className="project-title">Compétences</p>
              </div>
            </div>
          </div>
        </div>
      </div>
     { Object.keys(categorizedData).map((key, index) => {
    console.log(`Category: ${key}`);
    categorizedData[key].items.map((item, index) =>
      console.log(`Item ${index}: ${item}`)
    );
  })}
      <div className="window-frame">
        {skillData.map((category, index) => {
          return category === skillData.name ? (
            <section key={index}>
              <div className="section-title">
                <p>{category.name}</p>
              </div>
              {skillData.map((post, index) => {
                return (
                  <div className="section-content">
                    <div className="skill">
                      <div className="icon-skill"></div>
                      <p className="skill-name"></p>
                    </div>
                  </div>
                );
              })}
              {console.log(category.name)}
            </section>
          ) : null;
        })}
        {/* {skillData.map(({ element, category }, index) => {

          return categoryName === category.name? (
            <section key={index}>
              <div className="section-title">
                <p>{element.name}</p>
              </div>
              <div className="section-content">
                <div className="skill">
                  <div className="icon-skill"></div>
                  <p className="skill-name"></p>
                </div>
              </div>
            </section>
          )
        })} */}
      </div>
    </div>
  );
}

export default CompetencesWindow;
