import React, { useState } from 'react';
import '../index.css';
import SideBar from '../SideBar';
import Chevron from './../Icon/chevron-left.png';

const devIos = [
  {
    id: 1,
    name: 'Swift',
    img: './icons/swift.png',
    desc: 'Langage de programmation iOS.',
  },
  {
    id: 2,
    name: 'Swift UI',
    img: './icons/swiftUI.png',
    category: 'Développement iOS',
    desc: 'Framework Swift.',
  },
];

const devWeb = [
  {
    id: 1,
    name: 'React JS',
    img: './icons/react.png',
    category: 'Devéloppement Web',
    desc: 'Librairie JavaScript.',
  },
  {
    id: 2,
    name: 'JavaScript',
    img: './icons/js.png',
    category: 'Devéloppement Web',
    desc: 'Langage de programmation.',
  },
  {
    id: 3,
    name: 'CSS',
    img: './icons/css.png',
    category: 'Devéloppement Web',
    desc: 'Langage informatique présentation HTML.',
  },
  {
    id: 4,
    name: 'SASS',
    img: './icons/sass.png',
    category: 'Devéloppement Web',
    desc: 'Surcouche CSS.',
  },
  {
    id: 5,
    name: 'Bootstrap',
    img: './icons/bootstrap.png',
    category: 'Devéloppement Web',
    desc: 'Librairie HTML / CSS/ JS.',
  },
  {
    id: 6,
    name: 'GSAP',
    img: './icons/gsap.png',
    category: 'Devéloppement Web',
    desc: "Librairie d'animation JS.",
  },
];

const versioning = [
  {
    id: 1,
    name: 'Git / Github',
    img: './icons/github.png',
    category: 'Versioning',
    desc: 'Versioning.',
  },
];

const software = [
  {
    id: 1,
    name: 'Figma',
    img: './icons/figma.png',
    category: 'Software',
    desc: 'Outil de prototypage & design collaboratif.',
  },
];

function CompetencesWindow({ toggleTabs, setToggleTabs, matches }) {
  const toggleTab = (index) => {
    setToggleTabs(index);
  };

  return (
    <div>
      {matches ? (
        <div>
          <p className="cat-title ios">Developpement ios</p>
          <div className="skill-cat">
            {devIos.map((ios, index) => (
              <div className="skill" key={index}>
                <div className="skill-content">
                  <div className="icon-content">
                    <img src={ios.img} alt="icon" />
                  </div>
                  <p className="skill-name">
                    {ios.name}
                    <p className="skill-desc">{ios.desc}</p>
                  </p>
                </div>
              </div>
            ))}
          </div>
          <p className="cat-title web">Developpement Web</p>
          <div className="skill-cat">
            {devWeb.map((web) => (
              <div className="skill" id={web.id}>
                <div className="skill-content w">
                  <div className="icon-content">
                    <img src={web.img} alt="icon" />
                  </div>
                  <p className="skill-name">
                    {web.name}
                    <p className="skill-desc">{web.desc}</p>
                  </p>
                </div>
              </div>
            ))}
          </div>
          <p className="cat-title vers">Versioning</p>
          <div className="skill-cat">
            {versioning.map((vers) => (
              <div className="skill" id={vers.id}>
                <div className="skill-content">
                  <div className="icon-content">
                    <img src={vers.img} alt="icon" />
                  </div>
                  <p className="skill-name">
                    {vers.name}
                    <p className="skill-desc">{vers.desc}</p>
                  </p>
                </div>
              </div>
            ))}
          </div>
          <p className="cat-title soft">Software</p>
          <div className="skill-cat">
            {software.map((soft) => (
              <div className="skill" id={soft.id}>
                <div className="skill-content">
                  <div className="icon-content">
                    <img src={soft.img} alt="icon" />
                  </div>
                  <p className="skill-name">
                    {soft.name}
                    <p className="skill-desc">{soft.desc}</p>
                  </p>
                </div>
              </div>
            ))}
          </div>
        </div>
      ) : (
        // Mobile

        <div>
          <div className="nav-bar-container">
            <div className="back" onClick={() => toggleTab(0)}>
              <img src={Chevron} alt="" />
              <p className="nav-bar-txt">Retour</p>
            </div>
          </div>
          <p className="cat-title ios">Developpement ios</p>
          <div className="skill-cat">
            {devIos.map((ios, index) => (
              <div className="skill" key={index}>
                <div className="skill-content">
                  <div className="icon-content">
                    <img src={ios.img} alt="icon" />
                  </div>
                  <p className="skill-name">
                    {ios.name}
                    <p className="skill-desc">{ios.desc}</p>
                  </p>
                </div>
              </div>
            ))}
          </div>
          <p className="cat-title web">Developpement Web</p>
          <div className="skill-cat">
            {devWeb.map((web) => (
              <div className="skill" id={web.id}>
                <div className="skill-content w">
                  <div className="icon-content">
                    <img src={web.img} alt="icon" />
                  </div>
                  <p className="skill-name">
                    {web.name}
                    <p className="skill-desc">{web.desc}</p>
                  </p>
                </div>
              </div>
            ))}
          </div>
          <p className="cat-title vers">Versioning</p>
          <div className="skill-cat">
            {versioning.map((vers) => (
              <div className="skill" id={vers.id}>
                <div className="skill-content">
                  <div className="icon-content">
                    <img src={vers.img} alt="icon" />
                  </div>
                  <p className="skill-name">
                    {vers.name}
                    <p className="skill-desc">{vers.desc}</p>
                  </p>
                </div>
              </div>
            ))}
          </div>
          <p className="cat-title soft">Software</p>
          <div className="skill-cat">
            {software.map((soft) => (
              <div className="skill" id={soft.id}>
                <div className="skill-content">
                  <div className="icon-content">
                    <img src={soft.img} alt="icon" />
                  </div>
                  <p className="skill-name">
                    {soft.name}
                    <p className="skill-desc">{soft.desc}</p>
                  </p>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default CompetencesWindow;
