import React, { useState, useMemo } from 'react';
import '../index.css';
import Hammer from './Icon/hammer.png';

function CompetencesWindow() {
  const [skillList, setSkillList] = useState([]);

  const [selectedCategory, setSelectedCategory] = useState();

  function getFilteredList() {
    // Avoid filter when selectedCategory is null
    if (!selectedCategory) {
      return skillList;
    }
    return skillList.filter((item) => item.category === selectedCategory);
  }

  // Avoid duplicate function calls with useMemo
  var filteredList = useMemo(getFilteredList, [selectedCategory, skillList]);

  function handleCategoryChange(event) {
    setSelectedCategory(event.target.value);
  }

  const skillData = [
    {
      id: 1,
      name: 'Swift',
      img: '/img/crypto.png',
      category: 'Développement iOS',
    },
    {
      id: 2,
      name: 'Swift UI',
      img: '/img/crypto.png',
      category: 'Développement iOS',
    },
    {
      id: 3,
      name: 'React JS',
      img: '/img/crypto.png',
      category: 'Devéloppement Web',
    },
    {
      id: 4,
      name: 'JavaScript',
      img: '/img/crypto.png',
      category: 'Devéloppement Web',
    },
    {
      id: 5,
      name: 'CSS',
      img: '/img/crypto.png',
      category: 'Devéloppement Web',
    },
    {
      id: 6,
      name: 'SASS',
      img: '/img/crypto.png',
      category: 'Librairie / Frameworks',
    },
    {
      id: 7,
      name: 'Bootstrap',
      img: '/img/crypto.png',
      category: 'Librairie / Frameworks',
    },
    {
      id: 8,
      name: 'Git / Github',
      img: '/img/crypto.png',
      category: 'Versionning',
    },
    {
      id: 9,
      name: 'Figma',
      img: '/img/crypto.png',
      category: 'Software',
    },
  ];

  return (
    <div className="apple-window-container">
      <div className="sidebar">
        <div className="traffic-lights">
          <div className="red"></div>
          <div className="orange"></div>
          <div className="green"></div>
        </div>
        <div className="sidebar-menu">
          <div className="sidebar-title">
            <div className="sidebar-project">
              <div className="project-name">
                <img className="menubar" src={Hammer} alt="menubar-icon" />
                <p className="project-title">Compétences</p>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="window-frame">
        {filteredList.map((element, index) => {
          return (
            <section>
              <div className="section-title">
                <p>{element.name}</p>
              </div>
              <div className="section-content">
                <div className="skill">
                  <div className="icon-skill"></div>
                  <p className="skill-name"></p>
                </div>
              </div>
            </section>
          );

        })}
      </div>
    </div>
  );
}

export default CompetencesWindow;
