import React, { useState } from 'react';
import './index.css';
import CompetencesWindow from './Compétences';
import FormationWindow from './Formations';
import WorkWindow from './Work';

function Window({ toggleTabs, matches }) {
  return (
    <div className="window-frame">
      {toggleTabs === 1 && <CompetencesWindow matches={matches} />}
      {toggleTabs === 2 && <FormationWindow matches={matches} />}
      {toggleTabs === 4 && <WorkWindow matches={matches} />}
    </div>
  );
}

export default Window;
