import React, { useState } from 'react';
import '../index.css';
import Hammer from './Icon/hammer.png';

const skillData = [
  {
    id: 1,
    name: 'Swift',
    img: '/img/crypto.png',
    category: 'Développement iOS',
  },
  {
    id: 2,
    name: 'Swift UI',
    img: '/img/crypto.png',
    category: 'Développement iOS',
  },
  {
    id: 3,
    name: 'React JS',
    img: '/img/crypto.png',
    category: 'Devéloppement Web',
  },
  {
    id: 4,
    name: 'JavaScript',
    img: '/img/crypto.png',
    category: 'Devéloppement Web',
  },
  {
    id: 5,
    name: 'CSS',
    img: '/img/crypto.png',
    category: 'Devéloppement Web',
  },
  {
    id: 6,
    name: 'SASS',
    img: '/img/crypto.png',
    category: 'Librairie / Frameworks',
  },
  {
    id: 7,
    name: 'Bootstrap',
    img: '/img/crypto.png',
    category: 'Librairie / Frameworks',
  },
  {
    id: 8,
    name: 'Git / Github',
    img: '/img/crypto.png',
    category: 'Versionning',
  },
  {
    id: 9,
    name: 'Figma',
    img: '/img/crypto.png',
    category: 'Software',
  },
];

function skillRow({ skill }) {
  return (
    <div className="section-content">
      <div className="skill">
        <div className="icon-skill"></div>
        <p className="skill-name"></p>
      </div>
    </div>
  );
}

function categoryRow({ category }) {
  return
  <div>
    <div className="section-title">
      <p>{skillData.name}</p>
    </div>
  </div>;
}

function CompetencesWindow({ skills }) {
  const rows = [];
  let lastCategory = null;

  skillData.forEach((skill) => {
    if (skill.category !== lastCategory) {
      lastCategory = skill.category;
      rows.push(<categoryRow category={skill.category} />);
    }
    rows.push(<skillRow skill={skill} />);
  });

  return (
    <div className="apple-window-container">
      <div className="sidebar">
        <div className="traffic-lights">
          <div className="red"></div>
          <div className="orange"></div>
          <div className="green"></div>
        </div>
        <div className="sidebar-menu">
          <div className="sidebar-title">
            <div className="sidebar-project">
              <div className="project-name">
                <img className="menubar" src={Hammer} alt="menubar-icon" />
                <p className="project-title">Compétences</p>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="window-frame">{rows}</div>
    </div>
  );
}

export default CompetencesWindow;
