import React, { useState } from 'react';
import '../index.css';
import Hammer from './Icon/hammer.png';

const devIos = [
  {
    id: 1,
    name: "Swift",
    img: "./icons/swift.png"
  },
  {
    id: 2,
    name: "Swift UI",
    img: "./icons/swiftUI.png",
    category: "Développement iOS"
  }
];

const devWeb = [
  {
    id: 1,
    name: "React JS",
    img: "./icons/react.png",
    category: "Devéloppement Web"
  },
  {
    id: 2,
    name: "JavaScript",
    img: "./icons/js.png",
    category: "Devéloppement Web"
  },
  {
    id: 3,
    name: "CSS",
    img: "./icons/css.png",
    category: "Devéloppement Web"
  },
  {
    id: 4,
    name: "SASS",
    img: "./icons/sass.png",
    category: "Devéloppement Web"
  },
  {
    id: 5,
    name: "Bootstrap",
    img: "./icons/bootstrap.png",
    category: "Devéloppement Web"
  }
];

const versionning = [
  {
    id: 1,
    name: "Git / Github",
    img: "./icons/bootstrap.png",
    category: "Versionning"
  }
];

const software = [
  {
    id: 1,
    name: "Figma",
    img: "./icons/figma.png",
    category: "Software"
  }
];

function CompetencesWindow({ skills }) {
  function SkillRow({ skill }) {
    return (
      <div className="col">
        <div>
          <img classname="icon" src={skill.img} alt="icon" />
        </div>
        <div>
          <p className="skill-name">{skill.name}</p>
        </div>
      </div>
    );
  }

  function CategoryRow({ category, skill }) {
    return (
      <div>
        <div>
          <p>{category}</p>
          <div>{skill}</div>
        </div>
      </div>
    );
  }
  const rows = [];
  let lastCategory = null;

  skillData.forEach((skill) => {
    if (skill.category !== lastCategory) {
      lastCategory = skill.category;
      rows.push(<CategoryRow category={skill.category} />);
    }
    rows.push(<SkillRow skill={skill} />);
  });

  return (
    <div className="apple-window-container">
      <div className="sidebar">
        <div className="traffic-lights">
          <div className="red"></div>
          <div className="orange"></div>
          <div className="green"></div>
        </div>
        <div className="sidebar-menu">
          <div className="sidebar-title">
            <div className="sidebar-project">
              <div className="project-name">
                <img className="menubar" src={Hammer} alt="menubar-icon" />
                <p className="project-title">Compétences</p>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="window-frame">
        <div>

        </div>
      </div>
    </div>
  );
}

export default CompetencesWindow;
