import React from 'react';
import '../index.css';

function FormationFolder({
  CompetencesIsActive,
  CompetencesSetActive,
  toggleTabs,
  setToggleTabs,
  FormationsIsActive,
  FormationsSetActive,
}) {
  const toggleTab = (index) => {
    setToggleTabs(index);
  };

  const toggle = () => {
    CompetencesSetActive(!CompetencesIsActive);
  };

  const competences = [
    { id: 1, name: 'Formations', img: './Icons/folder.png' },
  ];

  return (
    <div className="folder">
      {competences.map((folder, index) => (
        <div className="folder-content" key={index} onClick={toggle}>
          <div className="folder-icon" onClick={() => toggleTab(2)}>
            <img className="i-folder" src={folder.img} alt="folder" />
          </div>
          <div className="folder-name">{folder.name}</div>
        </div>
      ))}
    </div>
  );
}

export default FormationFolder;
