import React from 'react';
import '../index.css';
import Hammer from './Icon/hammer.png';

function CompetencesWindow() {
  const skillData = [
    {
      id: 1,
      name: 'Swift',
      img: '/img/crypto.png',
      category: 'Développement iOS',
    },
    {
      id: 2,
      name: 'Swift UI',
      img: '/img/crypto.png',
      category: 'Développement iOS',
    },
    {
      id: 3,
      name: 'React JS',
      img: '/img/crypto.png',
      category: 'Devéloppement Web',
    },
    {
      id: 4,
      name: 'JavaScript',
      img: '/img/crypto.png',
      category: 'Devéloppement Web',
    },
    {
      id: 5,
      name: 'CSS',
      img: '/img/crypto.png',
      category: 'Devéloppement Web',
    },
    {
      id: 6,
      name: 'SASS',
      img: '/img/crypto.png',
      category: 'Librairie / Frameworks',
    },
    {
      id: 7,
      name: 'Bootstrap',
      img: '/img/crypto.png',
      category: 'Librairie / Frameworks',
    },
    {
      id: 8,
      name: 'Git / Github',
      img: '/img/crypto.png',
      category: 'Versionning',
    },
    {
      id: 9,
      name: 'Figma',
      img: '/img/crypto.png',
      category: 'Software',
    },
  ];

  return (
    <div className="apple-window-container">
      <div className="sidebar" value={id}>
        <div className="traffic-lights">
          <div className="red"></div>
          <div className="orange"></div>
          <div className="green"></div>
        </div>
        <div className="sidebar-menu">
          <div className="sidebar-title">
            {name}
            <div className="sidebar-project">
              <div key={index} className="project-name">
                <img className="menubar" src={MenuBar} alt="menubar-icon" />
                <p className="project-title">{name.name}</p>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="window-frame"></div>
    </div>
  );
}

export default CompetencesWindow;
