import React, { useState } from 'react';
import '../index.css';

import List from './Icon/list.png';
import Square from './Icon/square.png';
import Trash from './Icon/trash.png';
import Edit from './Icon/edit.png';
import Text from './Icon/textformat.png';
import Check from './Icon/checkmark.png';
import Table from './Icon/tablecells.png';
import Lock from './Icon/lock.png';
import Photo from './Icon/photo.png';
import Person from './Icon/person.png';
import Share from './Icon/share.png';
import Chevrons from './Icon/chevrons.png';
import Search from './Icon/search.png';

// const formations = [
//   {
//     id: 1,
//     name: 'Apple Foundation Program',
//     description: 'Concevoir',
//     img: './icons/apple.png',
//   },
// ];

const icons = [{ id: 1, icon: Edit }];

function AboutWindow() {
  // const toggleClass = () => {
  //   FormationsSetActive(!FormationsIsActive);
  // };

  return (
    <div className={`apple-window-container`}>
      <div className="sidebar">
        <div className="traffic-lights">
          <div className="red"></div>
          <div className="orange"></div>
          <div className="green"></div>
        </div>
        <div className="sidebar-menu">
          <div className="sidebar-title">
            <div className="sidebar-project">
              <div className="project-name">
                <img className="menubar" alt="menubar-icon" />
                <p className="project-title">Formations</p>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="window-frame notes">
        <div className="notes-container">
          <div className="col-left">
            <div className="tab">
              <ul className="icons">
                <div className="left">
                  <li className="icon list">
                    <img src={List} alt="icon" />
                  </li>
                  <li className="icon square">
                    <img src={Square} alt="icon" />
                  </li>
                </div>
                <div className="right">
                  <li className="icon trash">
                    <img src={Trash} alt="icon" />
                  </li>
                </div>
              </ul>
            </div>
            <div className="notes-container">
              <div className="notes">
                <p className="text-title">A propos de moi</p>
              </div>
            </div>
          </div>
          <div className="col-right">
            <div className="tab">
              <ul className="icons">
                {icons.map((icon, index) => {
                  <li className="icon trash" key={index}>
                    <img src={icon.icon} alt="icon" />
                  </li>;
                })}
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AboutWindow;
