import React, { useState } from 'react';
import '../index.css';

const folders = [
  { id: 1, name: 'Compétences', img: './Icons/folder.png' },
  { id: 2, name: 'Formations', img: './Icons/folder.png' },
];

export const AllFolders = () => {
  // this state is used to store the INDEX of the card that is currently expanded
  const [expandedCard, setExpandedCard] = useState(null);

  return (
    <div>
      {folders.map((folder, index) => (
        <Folder
          folder={folder}
          key={folder.id}
          img={folder.img}
          // this prop passes the boolean value of whether the card is expanded or not
          isExpanded={expandedCard === index}
          // this prop receives the index of the card that is expanded and sets the state
          expandedCard={() => setExpandedCard(index)}
        />
      ))}
    </div>
  );
};

function Folder({ folder, isExpanded, expandedCard, isActive, setActive }) {
  const toggleClass = () => {
    setActive(!isActive);
  };

  return (
    <div className="folder">
      <div className="folder-content" onClick={toggleClass}>
        <div className="folder-icon">
          <img className="i-folder" src={folder.img} alt="folder" />
        </div>
        <div className="folder-name">{folder.name}</div>
      </div>
    </div>
  );
}

export default Folder;
